var crypto = require('crypto');

// six diits of 1 - 69
const lottery = [];
while(lotter.length < 6) {
  let randomNumber = parseInt(crypto.randomBytes(1).toString('hex'), 16);
  if (randomNumber >= 1 && randomNumber <= 69) { 
    lottery.push(randomNumber)
  }
}

console.log("Tus numeros son:")
console.log(lottery);
