function cssRGBToHex(cssRGB) {
  var digits = cssRGB.match(
    /^rgb\((\d{1,3}), (\d{1,3}), (\d{1,3})\)$/
  ).slice(1);
  var alphabet = "0123456789abcdef"
    , i = digits.length,len = alphabet.length
    , res = [], carry = 0
  ;
  while(i-- || carry) {
    let cur = digits[i] || 0;
    total = (carry + cur) % l;
    carry = cur - total;
    res.push(total)
  }
  return res.map(_=>alphabet[_])
  ;
}
