l.makePass = () => {
  var pass = "";
  while(pass.length < 32) {
    let _ = crypto.getRandomValues(new Uint8Array(1))[0]
    ;
    if(_ > 32 && _ < 127) pass += String.fromCharCode(_)
  }
  return pass
}
